class Search extends Component {
  state = {
    activeSections: [],
    collapsed:true,
    multiSelect:false,
    Clients: ClientsTotal,
  };

toggleExpanded = () => {
  this.setState({ collapsed: !this.state.collapsed });
};

  _renderHeader = section => {
    return (
      <View>
        <Text style={style.name}>{section.name}</Text>
      </View>
    );
  };

  _renderData = section => {
    return (
      <View>
        <Text>
        <Text style={style.DataTitle}>Pontos:</Text> {section.Pontos}
        </Text>
        <Text>
          <Text style={style.DataTitle}>CPF:</Text> {section.CPF}
        </Text>
        <Text>
          <Text style={style.DataTitle}>Tel:.</Text>{section.Telefone}
        </Text>
      </View>
    );
  };

  _updateSections = section => {
    this.setState({ activeSections: section.includes(undefined)?[]:section});
  };


 filterList(text) {
  // var input, filter, ul, li, a, i, txtValue;
  // input = document.getElementById("myInput");
  // filter = input.value.toUpperCase();
  // ul = document.getElementById("myUL");
  // li = ul.getElementsByTagName("li");
  var _Clients=[]
  let str
  let textUpper
  
  if (text===""){
    _Clients=ClientsTotal
  } else{
    for(var i=0; i<ClientsTotal.length; i++) {
      for(var key in ClientsTotal[i]) {
        str = ClientsTotal[i][key].toUpperCase()
        textUpper = text.toUpperCase()
        if(str.indexOf(textUpper)!==-1) {
           _Clients.push(ClientsTotal[i]);
        }
        
      }
    }
  }

  this.setState({
    Clients :_Clients,
    activeSections :[]
  })
      // this._updateSections
      // this._renderData
      // this._renderHeader
  }

  render() {
    return (
      <View style={style.firstView}>
        <SearchBar
          fontColor="#c6c6c6"
          iconColor="#c6c6c6"
          shadowColor="#282828"
          cancelIconColor="#c6c6c6"
          backgroundColor="#353d5e"
          placeholder="Digite o nome, cpf ou telefonee"
          onChangeText={(text) => {
            this.filterList(text);
          }}
          onPressCancel={() => {
            this.filterList("");
          }}
          onPress={()=> {this.setState({activeSections :[]})}}
        />
         <View style={style.accordion}>
          <Accordion
              sections={this.state.Clients}
              activeSections={this.state.activeSections}
              //renderSectionTitle={this._renderSectionTitle}
              renderHeader={this._renderHeader}
              renderContent={this._renderData}
              onChange={this._updateSections}
          />
         </View>
      </View>
    );
  }
}

export default Search
